import React, { useContext, useState, useEffect } from "react";
import { Card, Col, Image, Button } from "react-bootstrap";
import { Context } from "../index";
import { observer } from "mobx-react-lite";

import UpdateProduct from "../components/modals/UpdateProduct";
import ConfirmProdDelete from "../components/modals/ConfirmProdDelete";

import { addItem, getCart } from "../http/cart_cartItemAPI"; // getCart - для подгрузки корзины с сервера

const ProductItem = observer(({ product }) => {
  const { user } = useContext(Context);
  const { cart } = useContext(Context);

  const [updateVisible, setUpdateVisible] = useState(false);
  const [deleteVisible, setDeleteVisible] = useState(false);
  const [cartItem, setCartItem] = useState(null); // Локальное состояние для товара в корзине
  const [loading, setLoading] = useState(true); // Состояние загрузки корзины

  // Подгружаем корзину с сервера и обновляем состояние в MobX при монтировании компонента
  useEffect(() => {
    const loadCartData = async () => {
      try {
        const cartData = await getCart(); // getCart() - API для получения корзины с сервера
        cart.setCartItems(cartData.CartItems); // Обновляем состояние корзины в MobX
        setLoading(false); // Завершаем загрузку
      } catch (error) {
        console.error("Ошибка при загрузке корзины:", error);
        setLoading(false); // Завершаем загрузку в случае ошибки
      }
    };

    loadCartData();
  }, [cart]); // Перезапускаем при изменении состояния корзины

  // Обновляем локальное состояние cartItem, когда cart.cartItems обновляется
  useEffect(() => {
    const existingItem = cart.cartItems.find((item) => item.product_id === product.id);
    setCartItem(existingItem); // Обновляем локальное состояние для товара в корзине
  }, [cart.cartItems, product.id]); // Следим за изменениями cartItems

  // Функция для добавления товара в корзину
  const AddItemInCart = async () => {
    try {
      const data = await addItem({ product_id: product.id });

      // Отфильтровываем только нужные поля и обновляем состояние корзины
      const filteredItems = data.map((item) => ({
        id: item.id,
        cart_id: item.cart_id,
        product_id: item.product_id,
        quantity: item.quantity,
      }));

      // Обновляем cartItems в MobX
      filteredItems.forEach((newItem) => {
        cart.setCartItem(newItem); // Используем MobX-метод для обновления состояния
      });

      cart.setCartItems([...cart.cartItems]); // Сохраняем обновленные данные в store
    } catch (e) {
      alert(e.response?.data?.message);
    }
  };

  if (loading) {
    // Возвращаем спиннер, пока данные корзины не загружены
    return <div>Загрузка корзины...</div>;
  }

  return (
    <Col md={4} className="d-flex mb-4">
      <Card className="w-100 d-flex flex-column border-secondary">
        <Image
          src={process.env.REACT_APP_API_URL + product.image}
          alt={product.name}
          style={{ width: "100%", height: 300, borderRadius: 10, objectFit: "contain" }} // contain чтобы изображения не сжались
          className="p-1"
        />
        <Card.Body className="d-flex flex-column">
          <Card.Title className="mb-1">{product.name}</Card.Title>

          <Card.Text className="mt-2 flex-grow-1">
            <strong>Описание:</strong> {product.description}
            <br />
            <strong>Цена:</strong> {product.price} ₽
            <br />
            <strong>В наличии:</strong> {product.stock} шт.
          </Card.Text>

          {!cartItem || cartItem.quantity === 0 ? (
            <Button variant="outline-primary" className="mt-auto w-100" onClick={AddItemInCart}>
              Добавить в корзину
            </Button>
          ) : (
            <Button variant="outline-primary" className="mt-auto w-100" onClick={AddItemInCart}>
              Обновить в корзине
            </Button>
          )}

          {/* Для админов кнопочки */}
          {user.user.role === "admin" && (
            <>
              <Button variant="outline-primary" className="mt-2 w-100 " onClick={() => setUpdateVisible(true)}>
                Обновить информацию
              </Button>
              <UpdateProduct show={updateVisible} onHide={() => setUpdateVisible(false)} productId={product.id} />
            </>
          )}

          {user.user.role === "admin" && (
            <>
              <Button variant="danger" className="mt-2 w-100 " onClick={() => setDeleteVisible(true)}>
                Удалить
              </Button>
              <ConfirmProdDelete show={deleteVisible} onHide={() => setDeleteVisible(false)} productId={product.id} />
            </>
          )}
        </Card.Body>
      </Card>
    </Col>
  );
});

export default ProductItem;
