// для навигации по страницам
import React from "react";
import { Route, Redirect, Routes } from "react-router-dom";
import { authRoutes, publicRoutes } from "../routes";

const AppRouter = () => {
  const isAuth = false;

  return (
    <Routes>
      {isAuth === true &&
        authRoutes.map(
          ({ path, Component }) => <Route key={path} path={path} component={Component} exact /> // exact говорит о том, что ключ должен полностью совпадать
        )}
      {publicRoutes.map(
        ({ path, Component }) => (
          <Route key={path} path={path} component={Component} exact />
        ) // exact говорит о том, что ключ должен полностью совпадать
      )}
      {/* Отображается, если маршрут не найден */}
      <Route path={path.NOT_FOUND_ROUTER} component={<NotFound />} />
    </Routes>
  ); // если ни один из маршрутов не отработает, то отработает самый последний в этом switch
};

export default AppRouter;
