import { makeAutoObservable } from "mobx";

export default class ProductStore {
  constructor() {
    this._category = [];
    this._product = [];
    this._selectedCategory = {};

    this._page = 1;
    this._totalCount = 0;
    this._limit = 6;
    makeAutoObservable(this);
  }

  // Установка категории
  setCategory(category) {
    this._category = category;
  }

  // Установка продуктов
  setProduct(products) {
    this._product = products;
  }

  // Установка выбранной категории с возможностью сброса страницы
  setSelectedCategory(type, resetPage = false) {
    if (resetPage) {
      this.setPage(1); // Сбрасываем страницу, если нужно
    }
    this._selectedCategory = type;
  }

  // Установка страницы пагинации
  setPage(page) {
    this._page = page;
  }

  // Установка общего количества продуктов
  setTotalCount(count) {
    this._totalCount = count;
  }

  get category() {
    return this._category;
  }

  get product() {
    return this._product;
  }

  get selectedCategory() {
    return this._selectedCategory;
  }

  get page() {
    return this._page;
  }

  get totalCount() {
    return this._totalCount;
  }

  get limit() {
    return this._limit;
  }
}
