import React, { useContext } from "react";
import { Context } from "../index";
import { Navbar, Nav, Container, Button } from "react-bootstrap";
import { NavLink } from "react-router-dom";
import { SHOP_ROUTER } from "../utils/constants";

const NavBar = () => {
  const { user } = useContext(Context);

  return (
    <Navbar bg="dark" data-bs-theme="dark">
      <Container>
        <NavLink to={SHOP_ROUTER} style={{ color: "white" }}>
          МагазинАвтозапчастей
        </NavLink>
        {user.isAuth ? (
          <Nav className="ml-auto">
            {/* ml-auto для отображения панели справа*/}
            <Button>Заказы</Button>
            <Button>Корзина</Button>
            <Button variant="outline-danger">Выйти</Button>
            <Button>Админ Панель</Button>
          </Nav>
        ) : (
          <Nav className="ml-auto">
            <Button variant="outline-danger">Авторизация</Button>
          </Nav>
        )}
      </Container>
    </Navbar>
  );
};

export default NavBar;
