import React from "react";
import { Modal, Button } from "react-bootstrap";
import SHOP_ROUTER from "../utils/constants";

const ConfirmDelete = ({ show, onHide, onDelete }) => {
  const handleDelete = () => {
    onDelete(); // вызываем функцию удаления аккаунта
    navigate(SHOP_ROUTER); // переход на главную
  };

  const handleClose = () => {
    onHide();
    navigate(SHOP_ROUTER); // можно убрать, если при закрытии не нужен переход
  };
  return (
    <Modal show={show} onHide={onHide} centered>
      <Modal.Header closeButton>
        <Modal.Title>Подтверждение</Modal.Title>
      </Modal.Header>
      <Modal.Body>Вы точно желаете удалить аккаунт? Это действие необратимо.</Modal.Body>
      <Modal.Footer>
        <Button variant="danger" onClick={handleDelete}>
          Да, удалить
        </Button>
        <Button variant="secondary" onClick={handleClose}>
          Закрыть
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default ConfirmDelete;
