// для работы с mobx
import { makeAutoObservable } from "mobx";

export default class CartStore {
  constructor() {
    this._cartItems = [];
    this._cart = [];
    makeAutoObservable(this);
  }

  // экшены
  setCartItems(items) {
    this._cartItems = items.map((item) => ({
      id: item.id,
      cart_id: item.cart_id,
      product_id: item.product_id,
      quantity: item.quantity,
    }));
  }

  setCart(cart) {
    this._cart = cart; // Сохраняем данные корзины (например, cart_id, user_id)
  }

  get cart() {
    return this._cart; // Возвращаем саму корзину
  }

  get cartItems() {
    return this._cartItems;
  }
}
