import React, { useState, useEffect } from "react";
import { Form, Button, Modal, Dropdown } from "react-bootstrap";
import { updateProd, getCategory } from "../../http/product_categoryAPI";
import { observer } from "mobx-react-lite";

const UpdateProduct = observer(({ show, onHide, product }) => {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [stock, setStock] = useState("");

  // useEffect(() => {
  //   getCategory().then((data) => productStore.setCategory(data));
  // }, []);

  const upProd = async () => {
    try {
      await updateProd(product.id, { name, description, price, stock });
      alert("Товар успешно обновлен!");
      onHide(); // закрываем модалку после успешного обновления
      window.location.reload(); // можно лучше перезагрузить товары через MobX, но для начала так
    } catch (e) {
      alert("Ошибка при обновлении товара");
    }
  };

  return (
    <Modal size="lg" centered onHide={onHide} show={show}>
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">Добавить продукт</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Control
            className="mb-2"
            placeholder={"Введите новое название продукта"}
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <Form.Control
            className="mb-2"
            placeholder={"Введите новое описание товара"}
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
          <Form.Control
            className="mb-2"
            type="number"
            min="0"
            placeholder={"Введите новую стоимость товара "}
            value={price}
            onChange={(e) => setPrice(e.target.value)}
          />
          <Form.Control
            className="mb-2"
            placeholder={"Введите товара в наличии"}
            value={stock}
            onChange={(e) => setStock(e.target.value)}
          />

          <Dropdown className="mb-3">
            <Dropdown.Toggle>{product.selectedCategory.category_name || "Выберите категорию"}</Dropdown.Toggle>
            <Dropdown.Menu>
              {product.category.map((categ) => (
                <Dropdown.Item onClick={() => product.setSelectedCategory(categ)} key={categ.category_id}>
                  {categ.category_name}
                </Dropdown.Item>
              ))}
            </Dropdown.Menu>
          </Dropdown>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="outline-success" onClick={upProd}>
          Обновить данные о продукте
        </Button>
        <Button variant="outline-danger" onClick={onHide}>
          Закрыть
        </Button>
      </Modal.Footer>
    </Modal>
  );
});

// id: 3,
//         name: "Антифриз NGN Long Life Antifreeze BS",
//         description: "Готовый (раствор), 5л",
//         price: 1300,
//         stock: 28,
//         image:
//           "https://avatars.mds.yandex.net/i?id=b5c9ba6179133899aa78d2a00319eb45bb6d158d-7751470-images-thumbs&n=13",
//         category_id: 3,

export default UpdateProduct;
