import React, { useContext, useState } from "react";
import { useNavigate } from "react-router-dom";
import { Modal, Button } from "react-bootstrap";
import { observer } from "mobx-react-lite";
import { Context } from "../../index";
import { deleteUser } from "../../http/userAPI";

import { SHOP_ROUTER } from "../../utils/constants";

const ConfirmDelete = observer(({ show, onHide, onDelete }) => {
  const navigate = useNavigate();
  const { user } = useContext(Context);

  // const handleDelete = () => {
  //   onDelete(); // вызываем функцию удаления аккаунта
  //   navigate(SHOP_ROUTER); // переход на главную
  // };

  const handleDelete = async () => {
    try {
      await deleteUser(); // отправка запроса на удаление
      user.logout(); // очистка store и токена
      setShowDeleteModal(false); // закрыть модалку
      navigate(SHOP_ROUTER); // редирект на главную (или LOGIN_ROUTER)
    } catch (error) {
      alert("Ошибка при удалении аккаунта");
    }
  };

  return (
    <Modal show={show} onHide={onHide} centered>
      <Modal.Header closeButton>
        <Modal.Title>Подтверждение</Modal.Title>
      </Modal.Header>
      <Modal.Body>Вы точно желаете удалить аккаунт? Это действие необратимо.</Modal.Body>
      <Modal.Footer>
        <Button variant="danger" onClick={handleDelete}>
          Да, удалить
        </Button>
        <Button variant="secondary" onClick={onHide}>
          Закрыть
        </Button>
      </Modal.Footer>
    </Modal>
  );
});

export default ConfirmDelete;
