import { makeAutoObservable } from "mobx";

export default class ProductStore {
  constructor() {
    this._category = [];
    this._product = this.loadProductsFromLocalStorage(); // Загружаем продукты из локального хранилища, если они есть
    this._selectedCategory = {};
    makeAutoObservable(this);
  }

  // Экшены - функции изменяющие состояние
  setCategory(category) {
    this._category = category;
  }

  setProduct(products) {
    this._product = products;
    this.saveProductsToLocalStorage(products); // Сохраняем продукты в localStorage
  }

  setSelectedCategory(type) {
    this._selectedCategory = type;
  }

  // Для получения переменных из состояния
  get category() {
    return this._category;
  }

  get product() {
    return this._product;
  }

  get selectedCategory() {
    return this._selectedCategory;
  }

  // Метод для сохранения продуктов в localStorage
  saveProductsToLocalStorage(products) {
    localStorage.setItem("products", JSON.stringify(products)); // Сохраняем продукты в формате JSON
  }

  // Метод для загрузки продуктов из localStorage
  loadProductsFromLocalStorage() {
    const products = localStorage.getItem("products");
    return products ? JSON.parse(products) : []; // Если данные есть, парсим их, иначе возвращаем пустой массив
  }
}
