import { makeAutoObservable } from "mobx";

export default class CartStore {
  constructor() {
    this._cartItems = [];
    this._cart = null;
    makeAutoObservable(this);
  }

  // Экшены
  // Инициализация/массовое добавление в стор
  setCartItems(items) {
    this._cartItems = items.map((item) => ({
      id: item.id,
      cart_id: item.cart_id,
      product_id: item.product_id,
      quantity: item.quantity,
    }));
  }

  //   // Добавление одного товара или обновление quantity
  //   setCartItem(item) {
  //     const index = this._cartItems.findIndex((i) => i.product_id === item.product_id);

  //     if (index !== -1) {
  //       // Обновляем quantity через новый объект
  //       this._cartItems[index] = {
  //         ...this._cartItems[index],
  //         quantity: item.quantity,
  //       };
  //     } else {
  //       this._cartItems.push({
  //         id: item.id,
  //         cart_id: item.cart_id,
  //         product_id: item.product_id,
  //         quantity: item.quantity || 1,
  //       });
  //     }
  //   }

  setCart(cart) {
    this._cart = {
      id: cart.id,
      user_id: cart.user_id,
    };
  }

  // Геттеры
  get cart() {
    return this._cart;
  }

  get cartItems() {
    return this._cartItems;
  }
}
