import React, { useContext, useState } from "react";
import { Card, Col, Image, Button } from "react-bootstrap";
import { Context } from "../index";

import UpdateProduct from "../components/modals/UpdateProduct";
import ConfirmProdDelete from "../components/modals/ConfirmProdDelete";

import { addItem } from "../http/cart_cartItemAPI";

const ProductItem = ({ product }) => {
  const { user } = useContext(Context);
  const [updateVisible, setUpdateVisible] = useState(false);
  const [deleteVisible, setDeleteVisible] = useState(false);

  const [cartQuantity, setCartQuantity] = useState(0);
  const [cartItemId, setCartItemId] = useState(null);

  const AddItemInCart = async () => {
    try {
      await addItem({ product_id: product.id });
      alert("Товар добавлен в корзину");
    } catch (e) {
      alert(e.response?.data?.message || "Ошибка при добавлении");
    }
  };

  return (
    <Col md={4} className="d-flex mb-4">
      <Card className="w-100 d-flex flex-column border-secondary">
        <Image
          src={process.env.REACT_APP_API_URL + product.image}
          alt={product.name}
          style={{ width: "100%", height: 300, borderRadius: 10, objectFit: "contain" }} // contain чтобы изображения не жмыхало
          className="p-1"
        />
        <Card.Body className="d-flex flex-column">
          <Card.Title className="mb-1">{product.name}</Card.Title>

          <Card.Text className="mt-2 flex-grow-1">
            <strong>Описание:</strong> {product.description}
            <br />
            <strong>Цена:</strong> {product.price} ₽
            <br />
            <strong>В наличии:</strong> {product.stock} шт.
          </Card.Text>

          {cartQuantity === 0 ? (
            <Button variant="outline-primary" className="mt-auto w-100" onClick={AddItemInCart}>
              Добавить в корзину
            </Button>
          ) : (
            <div className="d-flex justify-content-between align-items-center mt-auto w-100">
              <Button variant="outline-secondary" onClick={decreaseQuantity}>
                -
              </Button>
              <span>{cartQuantity}</span>
              <Button variant="outline-secondary" onClick={increaseQuantity}>
                +
              </Button>
            </div>
          )}
          {/* Для админов кнопочки */}
          {user.user.role === "admin" && (
            <>
              <Button variant="outline-primary" className="mt-2 w-100 " onClick={() => setUpdateVisible(true)}>
                Обновить информацию
              </Button>
              <UpdateProduct show={updateVisible} onHide={() => setUpdateVisible(false)} productId={product.id} />
            </>
          )}
          {user.user.role === "admin" && (
            <>
              <Button variant="danger" className="mt-2 w-100 " onClick={() => setDeleteVisible(true)}>
                Удалить
              </Button>
              <ConfirmProdDelete show={deleteVisible} onHide={() => setDeleteVisible(false)} productId={product.id} />
            </>
          )}
        </Card.Body>
      </Card>
    </Col>
  );

  //   id: 2,
  //         name: "Аккумулятор DELTA CT12201",
  //         description: "Обратная полярность, 20А",
  //         price: 6300,
  //         stock: 13,
  //         image: "b7ac01ac-4ad0-432f-a0ef-3d79170cddad.jpg",
  //         category_id: 2,
};

export default ProductItem;
