import React from "react";
import { Button, Card, Container, Form, FormControl, Row } from "react-bootstrap";
import { NavLink } from "react-router-dom";
import { REGISTRATION_ROUTER } from "../utils/constants";

const Auth = () => {
  return (
    <Container className="d-flex justify-content-center align-items-center" style={{ height: window.innerHeight - 54 }}>
      {/*карточка с рамкой*/}
      <Card style={{ width: 600 }} className="p-5">
        <h2 className="m-auto">Авторизация</h2>
        <Form className="d-flex flex-column">
          <Form.Control className="mt-4" placeholder="Введите Ваш email..." />
          <Form.Control className="mt-4" placeholder="Введите Ваш пароль..." />
          <Button className="mt-4 align-self-end" variant="outline-success">
            Войти
          </Button>
          <Row>
            <div>
              Нет аккаунта? <NavLink to={{ REGISTRATION_ROUTER }}>Зарегистрируйтесь</NavLink>
            </div>

            <Button variant="outline-success">Регистрация</Button>
          </Row>
        </Form>
      </Card>
    </Container>
  );
};

export default Auth;
