import { makeAutoObservable } from "mobx";

export default class CartStore {
  constructor() {
    this._cartItems = [];
    this._cart = null;
    makeAutoObservable(this);
  }

  setCartItem(item) {
    const index = this._cartItems.findIndex((cartItem) => cartItem.product_id === item.product_id);
    const normalizedItem = {
      id: item.id,
      cart_id: item.cart_id,
      product_id: item.product_id,
      quantity: item.quantity,
    };

    if (index !== -1) {
      this._cartItems[index] = normalizedItem;
    } else {
      this._cartItems.push(normalizedItem);
    }
  }

  setCart(cart) {
    this._cart = {
      id: cart.id,
      user_id: cart.user_id,
    };
  }

  // Геттеры
  get cart() {
    return this._cart;
  }

  get cartItems() {
    return this._cartItems;
  }
}
