import React, { useContext } from "react";
import { Pagination } from "react-bootstrap";
import { observer } from "mobx-react-lite";
import { Context } from "../index";

const PaginationBar = observer(() => {
  const { product } = useContext(Context);

  // Проверяем, что totalCount и limit есть и больше 0
  const pageCount = product.totalCount && product.limit ? Math.ceil(product.totalCount / product.limit) : 1; // Округляем в большую сторону
  const pages = [];

  // Формируем массив страниц
  for (let index = 0; index < pageCount; index++) {
    pages.push(index + 1);
  }

  return (
    <Pagination className="mt-3 justify-content-center">
      {/* Пагинация отображается только если есть страницы */}
      {pages.length > 1 &&
        pages.map((page) => (
          <Pagination.Item key={page} active={product.page === page} onClick={() => product.setPage(page)}>
            {page}
          </Pagination.Item>
        ))}
    </Pagination>
  );
});

export default PaginationBar;
