import React, { useContext, useEffect } from "react";
import { observer } from "mobx-react-lite";
import { Container, Row, Col, Card, Image, Button } from "react-bootstrap";
import { Context } from "../index";
import { getCart, decreaseItem, removeItem, addItem } from "../http/cart_cartItemAPI";

const Cart = observer(() => {
  const { cart, product } = useContext(Context);

  const cartItems = cart.cartItems || [];
  const products = product.product || [];

  useEffect(() => {
    const fetchCart = async () => {
      try {
        const data = await getCart();
        cart.setCart(data);
        cart.setCartItems(data.CartItems);
      } catch (error) {
        console.error("Ошибка при загрузке корзины:", error);
      }
    };

    fetchCart();
  }, [cart]);

  const totalPrice = cartItems.reduce((sum, item) => {
    const prod = products.find((p) => p.id === item.product_id);
    return sum + (prod?.price || 0) * item.quantity;
  }, 0);

  const increase = async (item) => {
    const updated = await addItem({ product_id: item.product_id });
    updated.forEach(cart.setCartItem);
  };

  const decrease = async (item) => {
    const updated = await decreaseItem({ product_id: item.product_id });
    updated.forEach(cart.setCartItem);
  };

  const remove = async (item) => {
    const updated = await removeItem({ product_id: item.product_id });
    updated.forEach(cart.setCartItem);
  };

  return (
    <Container className="mt-4">
      <h2 className="text-center mb-4">Товары в корзине</h2>

      <Row className="justify-content-center">
        {cartItems.length === 0 ? (
          <div className="text-center">
            <h4>Ваша корзина пуста</h4>
          </div>
        ) : (
          cartItems.map((item) => {
            const productDetails = products.find((prod) => prod.id === item.product_id);
            if (!productDetails) return null;

            return (
              <Col key={item.id} md={4} className="d-flex mb-4">
                <Card className="w-100 border-secondary">
                  <Image
                    src={process.env.REACT_APP_API_URL + productDetails.image}
                    alt={productDetails.name}
                    style={{ width: "100%", height: 200, objectFit: "contain", borderRadius: 10 }}
                    className="p-2"
                  />
                  <Card.Body className="d-flex flex-column">
                    <Card.Title>{productDetails.name}</Card.Title>
                    <Card.Text className="flex-grow-1">
                      <strong>Цена:</strong> {productDetails.price} ₽<br />
                      <strong>Количество:</strong> {item.quantity} шт.
                    </Card.Text>

                    <div className="d-flex align-items-center justify-content-center my-2">
                      <Button variant="outline-danger" size="sm" className="me-2" onClick={() => decrease(item)}>
                        -
                      </Button>
                      <span>{item.quantity}</span>
                      <Button variant="outline-success" size="sm" className="ms-2" onClick={() => increase(item)}>
                        +
                      </Button>
                    </div>

                    <Button variant="outline-dark" onClick={() => remove(item)}>
                      Удалить
                    </Button>
                  </Card.Body>
                </Card>
              </Col>
            );
          })
        )}
      </Row>

      {cartItems.length > 0 && (
        <Card className="p-4 mt-4 text-center">
          <h4>Итого: {totalPrice} ₽</h4>
          <div className="mt-3 d-flex justify-content-center gap-3">
            <Button variant="outline-danger" onClick={() => cart.setCartItems([])}>
              Очистить корзину
            </Button>
            <Button variant="success">Создать заказ</Button>
          </div>
        </Card>
      )}
    </Container>
  );
});

export default Cart;
