import React, { useState } from "react";
import { Modal, Button, Card, Alert, Badge } from "react-bootstrap";

const UserOrdersModal = ({ show, onHide, userOrders }) => {
  const [statusMessage, setStatusMessage] = useState("");
  const [updatedOrders, setUpdatedOrders] = useState(userOrders || []);

  const updateStatus = (userId, orderId, newStatus) => {
    const updated = updatedOrders.map((user) => {
      if (user.id === userId) {
        return {
          ...user,
          orders: user.orders.map((order) => (order.id === orderId ? { ...order, status: newStatus } : order)),
        };
      }
      return user;
    });

    setUpdatedOrders(updated);
    setStatusMessage(`Статус заказа №${orderId} обновлён на "${newStatus}"`);
    setTimeout(() => setStatusMessage(""), 3000);
  };

  return (
    <Modal show={show} onHide={onHide} size="lg" centered scrollable>
      <Modal.Header closeButton>
        <Modal.Title>Информация о заказах пользователей</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        {statusMessage && <Alert variant="success">{statusMessage}</Alert>}
        {updatedOrders.map((user) => (
          <Card className="mb-4" key={user.id}>
            <Card.Header>
              <strong>{user.name}</strong> — <em>{user.email}</em>
            </Card.Header>
            <Card.Body>
              {user.orders.map((order) => (
                <Card className="mb-3" key={order.id}>
                  <Card.Body>
                    <div className="d-flex justify-content-between align-items-center">
                      <div>
                        <div>Заказ №{order.id}</div>
                        <div>
                          Сумма: <strong>{order.total}₽</strong>
                        </div>
                        <div>
                          Текущий статус:{" "}
                          <Badge bg="info" text="dark">
                            {order.status}
                          </Badge>
                        </div>
                      </div>
                      <div className="d-flex flex-column gap-2">
                        <Button variant="outline-warning" onClick={() => updateStatus(user.id, order.id, "pending")}>
                          Pending
                        </Button>
                        <Button variant="outline-primary" onClick={() => updateStatus(user.id, order.id, "shipped")}>
                          Shipped
                        </Button>
                        <Button variant="outline-success" onClick={() => updateStatus(user.id, order.id, "delivered")}>
                          Delivered
                        </Button>
                      </div>
                    </div>
                  </Card.Body>
                </Card>
              ))}
            </Card.Body>
          </Card>
        ))}
      </Modal.Body>
      <Modal.Footer>
        <Button variant="outline-danger" onClick={onHide}>
          Закрыть
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default UserOrdersModal;
