import React, { useContext, useEffect, useState } from "react";
import { Context } from "../index";
import { Container, Card, Row, Col, Image, Badge } from "react-bootstrap";

const OrdersPage = () => {
  const { cart, product, order } = useContext(Context);

  return (
    <Container className="mt-4">
      <h2 className="mb-4">Мои заказы</h2>
      {orders.map((order) => {
        const totalPrice = order.items.reduce((sum, item) => sum + item.price * item.quantity, 0);

        return (
          <Card className="mb-4 shadow-sm" key={order.id}>
            <Card.Body>
              <div className="d-flex justify-content-between align-items-center mb-3">
                <h5 className="mb-0">Заказ №{order.id}</h5>
                <Badge bg={getStatusVariant(order.status)}>{order.status.toUpperCase()}</Badge>
              </div>

              <hr />

              {/* Список товаров */}
              {order.items.map((item) => (
                <Row key={item.id} className="align-items-center py-2 border-bottom">
                  <Col xs={2}>
                    <Image src={item.image} alt={item.name} fluid rounded />
                  </Col>
                  <Col xs={5}>
                    <div className="fw-bold">{item.name}</div>
                  </Col>
                  <Col xs={2} className="text-center">
                    <small>Цена ед. товара:</small> <div>{item.price} ₽</div>
                  </Col>
                  <Col xs={3} className="text-center">
                    <small>Кол-во:</small> <div>{item.quantity} шт.</div>
                  </Col>
                </Row>
              ))}

              {/* Итог */}
              <div className="d-flex justify-content-end mt-3">
                <h5>
                  Итоговая сумма: <strong>{totalPrice} ₽</strong>
                </h5>
              </div>
            </Card.Body>
          </Card>
        );
      })}
    </Container>
  );
};

export default OrdersPage;
