import React, { useContext, useState } from "react";
import { useNavigate } from "react-router-dom";
import { Modal, Button } from "react-bootstrap";
import { Context } from "../../index";
import ConfirmDelete from "./ConfirmUserDelete";
import { SHOP_ROUTER } from "../../utils/constants";

const UserMenuModal = ({ show, onHide }) => {
  const navigate = useNavigate();
  const { user } = useContext(Context);
  const [showConfirmDelete, setShowConfirmDelete] = useState(false);

  const logOut = () => {
    user.setUser({}); // присваиваем пустой обьект
    user.setIsAuth(false);
    onHide();
    navigate(SHOP_ROUTER); // переход на главную
  };

  return (
    <>
      <Modal show={show} onHide={onHide} centered>
        <Modal.Header closeButton>
          <Modal.Title>Профиль</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>
            <strong>Имя:</strong> {"Имя не указано"}
          </p>
          <p>
            <strong>Email:</strong> {"Email не указан"}
          </p>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="outline-danger" onClick={() => setShowConfirmDelete(true)}>
            Удалить аккаунт
          </Button>
          <Button variant="outline-primary" onClick={() => logOut()}>
            Выйти
          </Button>
        </Modal.Footer>
      </Modal>

      {/* Модальное окно подтверждения удаления */}
      <ConfirmDelete
        show={showConfirmDelete}
        onHide={() => setShowConfirmDelete(false)}
        onDelete={() => {
          // Тут логика удаления аккаунта
          user.setIsAuth(false);
          setShowConfirmDelete(false);
          onHide();
        }}
      />
    </>
  );
};

export default UserMenuModal;
