import React, { useContext, useEffect } from "react";
import { Col, Container, Row } from "react-bootstrap";
import CategoryBar from "../components/CategoryBar";
import ProductList from "../components/ProductList";
import PaginationBar from "../components/PaginationBar";
import { observer } from "mobx-react-lite";
import { Context } from "../index";
import { getAllProduct, getCategory } from "../http/product_categoryAPI";
import { getCart } from "../http/cart_cartItemAPI";

const Shop = observer(() => {
  const { product, cart, user } = useContext(Context);

  useEffect(() => {
    // Загрузка категорий
    getCategory().then((data) => product.setCategory(data));

    // Загрузка товаров при изменении категории или страницы
    if (product.selectedCategory.id) {
      getAllProduct(product.selectedCategory.id, product.page, 3).then((data) => {
        product.setProduct(data.rows);
        product.setTotalCount(data.count);
      });
    }

    // Загрузка данных корзины для авторизованных пользователей
    if (user.isAuth) {
      getCart()
        .then((cartData) => {
          cart.setCart(cartData);
          cart.setCartItems(cartData.CartItems);
          console.log("Cart loaded in Shop:", cartData);
        })
        .catch((err) => {
          console.error("Ошибка при загрузке корзины в Shop:", err);
        });
    }
  }, [user.isAuth, product.page, product.selectedCategory]); // Следим за изменениями авторизации, страницы и категории

  return (
    <Container>
      <Row className="mt-3">
        <Col md={2} style={{ transform: "translateX(-100px)" }}>
          <CategoryBar />
        </Col>
        <Col md={10}>
          <ProductList />
          <PaginationBar />
        </Col>
      </Row>
    </Container>
  );
});

export default Shop;
