import { $authHost, $host } from "./index";
// реализуем регистрацию авторизацию и проверку токена на валидность

export const registration = async (name, email, password_hash) => {
  // админ на время
  const response = await $host.post("api/user/registration", { name, email, password_hash, role: "admin" });
  // { name, email, password_hash, role }
  return response;
};

export const login = async (email, password_hash) => {
  const response = await $host.post("api/user/login", { email, password_hash });
  return response;
};

export const check_auth = async () => {
  const response = await $host.post("api/user/auth");
  return response;
};
