import React, { useContext } from "react";
import { Card, Col, Image } from "react-bootstrap";

const ProductItem = ({ product }) => {
  return (
    <Col md={4}>
      <Card style={{ width: 330 }} border="secondary" className="px-1 py-1 mb-4">
        <Image width={320} height={300} src={product.image} /*{className="mx-1 my-1 "}*/ />
        <Card.Body>
          <Card.Title>{product.name}</Card.Title>
          <Card.Text>
            <strong>Описание: </strong> {product.description}
            <br />
            <strong>Цена:</strong> {product.price} ₽
            <br />
            <strong>В наличии:</strong> {product.stock} шт.
          </Card.Text>
        </Card.Body>
      </Card>
    </Col>
  );

  //   id: 2,
  //         name: "Аккумулятор DELTA CT12201",
  //         description: "Обратная полярность, 20А",
  //         price: 6300,
  //         stock: 13,
  //         image: "b7ac01ac-4ad0-432f-a0ef-3d79170cddad.jpg",
  //         category_id: 2,
};

export default ProductItem;
