import React, { useContext } from "react";
import { Container, Row, Col, Card, Button, Image, ListGroup } from "react-bootstrap";
import { Context } from "../index";

const Cart = () => {
  const { cart } = useContext(Context);

  const totalPrice = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <Container className="mt-4">
      <h2 className="text-center mb-4">Товары пользователя</h2>

      <Row className="justify-content-center">
        {cartItems.map((item) => (
          <Col key={item.id} md={4} className="d-flex mb-4">
            <Card className="w-100 border-secondary">
              <Image
                src={item.image}
                alt={item.name}
                style={{ width: "100%", height: 200, borderRadius: 10 }}
                className="p-2"
              />
              <Card.Body className="d-flex flex-column">
                <Card.Title className="mb-1">{item.name}</Card.Title>
                <Card.Text className="flex-grow-1">
                  <strong>Описание:</strong> {item.description}
                  <br />
                  <strong>Цена:</strong> {item.price} ₽
                  <br />
                  <strong>В наличии:</strong> {item.stock} шт.
                </Card.Text>

                <div className="d-flex align-items-center justify-content-center my-2">
                  <Button variant="outline-danger" size="sm" className="me-2">
                    -
                  </Button>
                  <span>{item.quantity}</span>
                  <Button variant="outline-success" size="sm" className="ms-2">
                    +
                  </Button>
                </div>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>

      <Card className="p-4 mt-4 text-center">
        <h4>Итого: {totalPrice} ₽</h4>
        <div className="mt-3 d-flex justify-content-center gap-3">
          <Button variant="outline-danger">Очистить корзину</Button>
          <Button variant="success">Создать заказ</Button>
        </div>
      </Card>
    </Container>
  );
};

export default Cart;
