import { makeAutoObservable } from "mobx";

export default class CartStore {
  constructor() {
    this._cartItems = [];
    this._cart = null;
    makeAutoObservable(this);
  }

  // Экшены
  setCartItems(items) {
    this._cartItems = items.map((item) => ({
      id: item.id,
      cart_id: item.cart_id,
      product_id: item.product_id,
      quantity: item.quantity,
    }));
  }

  setCartItem(updatedItem) {
    const index = this._cartItems.findIndex((item) => item.product_id === updatedItem.product_id);
    if (index !== -1) {
      // Обновляем только quantity
      this._cartItems[index].quantity = updatedItem.quantity;
    } else {
      // Добавляем новый товар
      this._cartItems.push(updatedItem);
    }
  }

  setCart(cart) {
    this._cart = {
      id: cart.id,
      user_id: cart.user_id,
    };
  }

  // Геттеры
  get cart() {
    return this._cart;
  }

  get cartItems() {
    return this._cartItems;
  }
}
