// для работы с mobx
import { makeAutoObservable } from "mobx";

export default class CartStore {
  constructor() {
    this._cartItems = [];
    makeAutoObservable(this);
  }

  // Actions
  setCartItems(items) {
    this._cartItems = items.map((item) => ({
      id: item.id,
      cart_id: item.cart_id,
      product_id: item.product_id,
      quantity: item.quantity,
    }));
  }

  //   addCartItem(item) {
  //     this._cartItems.push(item);
  //   }

  //   removeCartItem(cartItemId) {
  //     this._cartItems = this._cartItems.filter(item => item.id !== cartItemId);
  //   }

  //   clearCart() {
  //     this._cartItems = [];
  //   }

  // Computed
  get cartItems() {
    return this._cartItems;
  }

  get totalPrice() {
    return this._cartItems.reduce((sum, item) => sum + item.product.price * item.quantity, 0);
  }

  get totalItems() {
    return this._cartItems.reduce((sum, item) => sum + item.quantity, 0);
  }
}
