import React, { useContext, useEffect } from "react";
import { Col, Container, Row } from "react-bootstrap";
import CategoryBar from "../components/CategoryBar";
import ProductList from "../components/ProductList";
import { observer } from "mobx-react-lite";
import { Context } from "../index";
import { getAllProduct, getCategory } from "../http/productAPI";

const Shop = observer(() => {
  const { product } = useContext(Context);

  // хук для подгрузки, которая будет единожды при открытии страницы
  useEffect(() => {
    // если запрос на получение типов был успешным, то (then) передаем то, что вернулось из запроса
    getCategory().then((data) => product.setCategory(data));
    getAllProduct().then((data) => product.setCategory(data));
  }, []);
  return (
    // строка состоит из 12 колонок
    // md=3 для панели с отображением категории
    // md= 9 для самих товаров
    <Container>
      <Row className="mt-3">
        <Col md={2} style={{ transform: "translateX(-100px)" }}>
          <CategoryBar />
        </Col>
        <Col md={10}>
          <ProductList />
        </Col>
      </Row>
    </Container>
  );
});

export default Shop;
