import React from "react";
import { Container, Card, Row, Col, Image, Badge } from "react-bootstrap";

// Пример данных заказов
const orders = [
  {
    id: 101,
    total: 1500,
    status: "pending",
    items: [
      {
        id: 1,
        name: "Аккумулятор DELTA CT12201",
        price: 6300,
        quantity: 2,
        image: "https://via.placeholder.com/150",
      },
      {
        id: 2,
        name: "Свеча зажигания NGK",
        price: 800,
        quantity: 4,
        image: "https://via.placeholder.com/150",
      },
    ],
  },
  {
    id: 102,
    total: 3200,
    status: "shipped",
    items: [
      {
        id: 3,
        name: "Масло моторное Mobil 1",
        price: 3200,
        quantity: 1,
        image: "https://via.placeholder.com/150",
      },
    ],
  },
];

const getStatusVariant = (status) => {
  switch (status) {
    case "pending":
      return "warning";
    case "shipped":
      return "info";
    case "delivered":
      return "success";
    case "cancelled":
      return "danger";
    default:
      return "secondary";
  }
};

const OrdersPage = () => {
  return (
    <Container className="mt-4">
      <h2>Мои заказы</h2>
      <Row className="mt-3">
        {orders.map((order) => {
          // пересчитаем итоговую сумму по товарам
          const totalPrice = order.items.reduce((sum, item) => sum + item.price * item.quantity, 0);

          return (
            <Card className="mb-4" key={order.id}>
              <Card.Body>
                <Card.Title>
                  Заказ №{order.id} — <Badge bg={getStatusVariant(order.status)}>{order.status.toUpperCase()}</Badge>
                </Card.Title>

                <div className="mt-3">
                  {order.items.map((item) => (
                    <Row key={item.id} className="align-items-center mb-2">
                      <Col xs={2}>
                        <Image src={item.image} alt={item.name} width="70" height="70" rounded />
                      </Col>
                      <Col xs={5}>
                        <strong>{item.name}</strong>
                      </Col>
                      <Col xs={2}>{item.price} ₽</Col>
                      <Col xs={3}>Кол-во: {item.quantity} шт.</Col>
                    </Row>
                  ))}
                </div>

                {/* Итоговая сумма */}
                <div className="mt-4 d-flex justify-content-end">
                  <h5>
                    Итоговая сумма: <strong>{totalPrice} ₽</strong>
                  </h5>
                </div>
              </Card.Body>
            </Card>
          );
        })}
      </Row>
    </Container>
  );
};

export default OrdersPage;
