import React, { useContext, useEffect, useState } from "react";
import { observer } from "mobx-react-lite";
import { Container, Row, Col, Card, Image, Spinner } from "react-bootstrap";
import { Context } from "../index";
import { getCart } from "../http/cart_cartItemAPI";
import { getAllProduct } from "../http/product_categoryAPI";

const Cart = observer(() => {
  const { cart, product } = useContext(Context);
  const [loading, setLoading] = useState(true);

  const cartItems = cart.cartItems || [];
  const products = product.product || [];

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [cartData, productsData] = await Promise.all([getCart(), getAllProduct()]);

        cart.setCart(cartData); // сохраняем корзину
        cart.setCartItems(cartData.CartItems); // сохраняем товары в корзине
        product.setProduct(productsData); // обновляем продукты
      } catch (error) {
        console.error("Ошибка при загрузке данных корзины:", error);
      } finally {
        setLoading(false); // завершаем загрузку
      }
    };

    if (products.length === 0) {
      fetchData(); // Если нет продуктов, делаем запрос на сервер
    } else {
      setLoading(false); // Если продукты уже есть в state, сразу завершаем загрузку
    }
  }, [cart, product, products]); // добавляем products в зависимости

  if (loading) {
    return (
      <Container className="text-center mt-5">
        <Spinner animation="border" variant="primary" />
        <p className="mt-3">Загрузка корзины...</p>
      </Container>
    );
  }

  return (
    <Container className="mt-4">
      <h2 className="text-center mb-4">Товары в корзине</h2>

      <Row className="justify-content-center">
        {cartItems.length === 0 ? (
          <div className="text-center">
            <h4>Ваша корзина пуста</h4>
          </div>
        ) : (
          cartItems.map((item) => {
            const productDetails = products.find((prod) => prod.id === item.product_id);
            if (!productDetails) return null;

            return (
              <Col key={item.id} md={4} className="d-flex mb-4">
                <Card className="w-100 border-secondary">
                  <Image
                    src={process.env.REACT_APP_API_URL + productDetails.image}
                    alt={productDetails.name}
                    style={{
                      width: "100%",
                      height: 200,
                      objectFit: "contain",
                      borderRadius: 10,
                    }}
                    className="p-2"
                  />
                  <Card.Body>
                    <Card.Title>{productDetails.name}</Card.Title>
                    <Card.Text>
                      <strong>Цена:</strong> {productDetails.price} ₽<br />
                      <strong>Количество:</strong> {item.quantity} шт.
                    </Card.Text>
                  </Card.Body>
                </Card>
              </Col>
            );
          })
        )}
      </Row>
    </Container>
  );
});

export default Cart;
