import React, { useContext, useEffect, useState } from "react";
import { observer } from "mobx-react-lite";
import { Container, Row, Col, Card, Image, Spinner, Button, ButtonGroup } from "react-bootstrap";
import { Context } from "../index";
import { toJS } from "mobx";

import { getCart } from "../http/cart_cartItemAPI";
import { getAllProduct, getCategory } from "../http/product_categoryAPI";

import { addItem, decreaseItem } from "../http/cart_cartItemAPI";

const Cart = observer(() => {
  const { cart, product } = useContext(Context);
  const [loading, setLoading] = useState(true);

  const cartItems = cart.cartItems || [];
  const products = product.product || [];

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Загрузка категорий и продуктов
        const [categoryData, productData] = await Promise.all([getCategory(), getAllProduct()]);
        product.setCategory(categoryData);
        product.setProduct(productData);

        // Загрузка корзины, если пользователь авторизован

        const cartData = await getCart();
        cart.setCart(cartData);
        cart.setCartItems(cartData.CartItems);
        console.log("Cart loaded in Cart:", cartData);
      } catch (error) {
        console.error("Ошибка при загрузке данных:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return (
      <Container className="text-center mt-5">
        <Spinner animation="border" variant="primary" />
        <p className="mt-3">Загрузка корзины...</p>
      </Container>
    );
  }

  // проверяем, есть ли товар в корзине
  const cartItem = cart.cartItems.find((item) => item.product_id === product.id);

  const AddItemInCart = async () => {
    try {
      const item = await addItem({ product_id: product.id });
      // Если товар уже есть в корзине, обновляем его количество
      if (cartItem) {
        // Используем метод для обновления количества товара в корзине
        const newQuantity = cartItem.quantity + 1;
        cart.updateItemQuantity(product.id, newQuantity); // Обновляем количество в корзине
        console.log(toJS(cart.cartItems)); // Получаем чистую копию массива cartItems
      } else {
        cart.setCartItems(item); // Добавляем новый товар в корзину стор
        console.log(toJS(cart.cartItems)); // Получаем чистую копию массива cartItems
      }
    } catch (e) {
      alert(e.response?.data?.message || "Ошибка при добавлении в корзину");
    }
  };

  const DecreaseItemInCart = async () => {
    try {
      await decreaseItem(cartItem.id);

      // Используем метод для обновления количества товара в корзине
      const newQuantity = cartItem.quantity - 1;
      cart.updateItemQuantity(product.id, newQuantity); // Обновляем количество в корзине
      //console.log(toJS(cart.cartItems)); // Получаем чистую копию массива cartItems
    } catch (e) {
      alert(e.response?.data?.message || "Ошибка при добавлении в корзину");
    }
  };

  return (
    <Container className="mt-4">
      <h2 className="text-center mb-4">Товары в корзине</h2>

      <Row className="justify-content-center">
        {cartItems.length === 0 ? (
          <Card className="text-center p-4">
            <h4>В корзине отсутствуют товары</h4>
          </Card>
        ) : (
          cartItems.map((item) => {
            const productDetails = products.find((prod) => prod.id === item.product_id);
            if (!productDetails) return null;

            return (
              <Col key={item.id} md={4} className="d-flex mb-4">
                <Card className="w-100 border-secondary d-flex flex-column">
                  <Image
                    src={process.env.REACT_APP_API_URL + productDetails.image}
                    alt={productDetails.name}
                    style={{
                      width: "100%",
                      height: 200,
                      objectFit: "contain",
                      borderRadius: 10,
                    }}
                    className="p-2"
                  />
                  <Card.Body className="d-flex flex-column">
                    <Card.Title>{productDetails.name}</Card.Title>
                    <Card.Text>
                      <strong>Цена:</strong> {productDetails.price} ₽
                    </Card.Text>

                    <ButtonGroup className="my-2">
                      <Button variant="outline-danger" onClick={AddItemInCart}>
                        -
                      </Button>
                      <Button variant="light" disabled style={{ width: "100px" }}>
                        {item.quantity} шт.
                      </Button>
                      <Button variant="outline-success" onClick={DecreaseItemInCart}>
                        +
                      </Button>
                    </ButtonGroup>

                    <Button variant="outline-secondary" className="mt-auto">
                      Убрать товар из корзины
                    </Button>
                  </Card.Body>
                </Card>
              </Col>
            );
          })
        )}
      </Row>

      {cartItems.length > 0 && (
        <Row className="justify-content-center mt-4">
          <Col md={6} className="d-flex justify-content-between">
            <Button variant="danger">Очистить корзину</Button>
            <Button variant="primary">Оформить заказ</Button>
          </Col>
        </Row>
      )}
    </Container>
  );
});

export default Cart;
