import React, { useContext, useEffect } from "react";
import { Col, Container, Row } from "react-bootstrap";
import CategoryBar from "../components/CategoryBar";
import ProductList from "../components/ProductList";
import { observer } from "mobx-react-lite";
import { Context } from "../index";
import { getAllProduct, getCategory } from "../http/product_categoryAPI";
import { getCart } from "../http/cart_cartItemAPI"; // üëà –¥–æ–±–∞–≤–∏–ª–∏

const Shop = observer(() => {
  const { product, cart } = useContext(Context); // üëà –ø–æ–ª—É—á–∏–ª–∏ cart

  useEffect(() => {
    // –ó–∞–≥—Ä—É–∑–∫–∞ –∫–∞—Ç–µ–≥–æ—Ä–∏–π –∏ —Ç–æ–≤–∞—Ä–æ–≤
    getCategory().then((data) => product.setCategory(data));
    getAllProduct().then((data) => product.setProduct(data));

    // üëá –ó–∞–≥—Ä—É–∑–∫–∞ –∫–æ—Ä–∑–∏–Ω—ã
    getCart()
      .then((cartData) => {
        cart.setCart(cartData);
        cart.setCartItems(cartData.CartItems);
        console.log("Cart loaded in Shop:", cartData);
      })
      .catch((err) => {
        console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –∫–æ—Ä–∑–∏–Ω—ã –≤ Shop:", err);
      });
  }, []);

  return (
    <Container>
      <Row className="mt-3">
        <Col md={2} style={{ transform: "translateX(-100px)" }}>
          <CategoryBar />
        </Col>
        <Col md={10}>
          <ProductList />
        </Col>
      </Row>
    </Container>
  );
});

export default Shop;
