import React, { useContext } from "react";
import { observer } from "mobx-react-lite";
import { Context } from "../index";
import { ListGroup } from "react-bootstrap";

const CategoryBar = observer(() => {
  const { product } = useContext(Context);

  return (
    <ListGroup>
      <ListGroup.Item
        style={{ cursor: "pointer" }}
        active={product.selectedCategory === null}
        onClick={() => product.setSelectedCategory(null)}>
        Все категории
      </ListGroup.Item>

      {product.category.map((type) => (
        <ListGroup.Item
          key={type.category_id}
          style={{ cursor: "pointer" }}
          active={product.selectedCategory?.category_id === type.category_id}
          onClick={() => product.setSelectedCategory(type)} // передаём весь объект категории
        >
          {type.category_name}
        </ListGroup.Item>
      ))}
    </ListGroup>
  );
});

export default CategoryBar;
