import React, { useContext } from "react";
import { observer } from "mobx-react-lite";
import { Context } from "../index";
import { ListGroup } from "react-bootstrap";

const Category = observer(() => {
  const { product } = useContext(Context);

  // Проверка, что категории существуют и являются массивом
  if (!product.category || product.category.length === 0) {
    return <p>Загрузка категорий...</p>; // Показываем индикатор загрузки, если нет категорий
  }

  return (
    <ListGroup>
      {product.category.map((type) => (
        <ListGroup.Item
          style={{ cursor: "pointer" }}
          active={product.selectedCategory?.category_id === type.category_id}
          onClick={() => product.setSelectedCategory(type)}
          key={type.category_id}>
          {type.category_name}
        </ListGroup.Item>
      ))}
    </ListGroup>
  );
});

export default Category;
