import { makeAutoObservable } from "mobx";

export default class ProductStore {
  constructor() {
    this._category = [];
    this._product = []; // Продукты будут загружаться с сервера
    this._selectedCategory = тгдд;
    makeAutoObservable(this);
  }

  // Экшены - функции изменяющие состояние
  setCategory(category) {
    this._category = category;
  }

  setProduct(products) {
    this._product = products;
  }

  setSelectedCategory(type) {
    this._selectedCategory = type;
  }

  // Для получения переменных из состояния
  get category() {
    return this._category;
  }

  get product() {
    return this._product;
  }

  get selectedCategory() {
    return this._selectedCategory;
  }

  get filteredProducts() {
    // Вернуть отфильтрованные продукты по выбранной категории, если она выбрана
    if (this._selectedCategory === null) return this._product;
    return this._product.filter((product) => product.category_id === this._selectedCategory);
  }
}
