import React, { useContext, useEffect, useState } from "react";
import { Context } from "../index";
import { Container, Card, Row, Col, Image, Badge, Spinner } from "react-bootstrap";

import { getAllProduct } from "../http/product_categoryAPI";
import { getOrders } from "../http/orderAPI";

const getStatusVariant = (status) => {
  switch (status) {
    case "pending":
      return "warning";
    case "shipped":
      return "info";
    case "delivered":
      return "success";
    case "cancelled":
      return "danger";
    default:
      return "secondary";
  }
};

const OrdersPage = () => {
  const { product, order } = useContext(Context);
  const [loading, setLoading] = useState(true);

  const [ordersWithItems, setOrdersWithItems] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [productData, orderData] = await Promise.all([getAllProduct(), getOrders()]);

        product.setProduct(productData);

        // Установка заказов и товаров из заказов в стор
        order.setOrders(orderData);
        const allOrderItems = orderData.flatMap((order) => order.OrderItems);
        order.setOrderItems(allOrderItems);

        // Формируем заказы с деталями о продуктах
        const enrichedOrders = orderData.map((ord) => {
          const items = ord.OrderItems.map((item) => {
            const productData = productData.find((p) => p.id === item.product_id);
            return {
              id: item.id,
              product_id: item.product_id,
              quantity: item.quantity,
              name: productData?.name || "Неизвестный товар",
              price: productData?.price || 0,
              image: process.env.REACT_APP_API_URL + productData?.image,
            };
          });

          return {
            id: ord.id,
            status: ord.status,
            items,
          };
        });

        setOrdersWithItems(enrichedOrders);
      } catch (error) {
        console.error("Ошибка при загрузке данных:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return (
      <Container className="text-center mt-5">
        <Spinner animation="border" variant="primary" />
        <p className="mt-3">Загрузка заказов...</p>
      </Container>
    );
  }

  return (
    <Container className="mt-4">
      <h2 className="mb-4">Мои заказы</h2>
      {ordersWithItems.length === 0 ? (
        <p>У вас нет заказов.</p>
      ) : (
        ordersWithItems.map((order) => {
          const totalPrice = order.items.reduce((sum, item) => sum + item.price * item.quantity, 0);

          return (
            <Card className="mb-4 shadow-sm" key={order.id}>
              <Card.Body>
                <div className="d-flex justify-content-between align-items-center mb-3">
                  <h5 className="mb-0">Заказ №{order.id}</h5>
                  <Badge bg={getStatusVariant(order.status)}>{order.status.toUpperCase()}</Badge>
                </div>

                <hr />

                {/* Список товаров */}
                {order.items.map((item) => (
                  <Row key={item.id} className="align-items-center py-2 border-bottom">
                    <Col xs={2}>
                      <Image src={item.image} alt={item.name} fluid rounded />
                    </Col>
                    <Col xs={5}>
                      <div className="fw-bold">{item.name}</div>
                    </Col>
                    <Col xs={2} className="text-center">
                      <small>Цена:</small>
                      <div>{item.price} ₽</div>
                    </Col>
                    <Col xs={3} className="text-center">
                      <small>Кол-во:</small>
                      <div>{item.quantity} шт.</div>
                    </Col>
                  </Row>
                ))}

                {/* Итог */}
                <div className="d-flex justify-content-end mt-3">
                  <h5>
                    Итоговая сумма: <strong>{totalPrice} ₽</strong>
                  </h5>
                </div>
              </Card.Body>
            </Card>
          );
        })
      )}
    </Container>
  );
};

export default OrdersPage;
