import React, { useContext, useState, useEffect } from "react";
import { Context } from "./index";
import { BrowserRouter } from "react-router-dom";
import AppRouter from "./components/AppRouter";
import NavBar from "./components/NavBar";
import { observer } from "mobx-react-lite";
import { Spinner } from "react-bootstrap";

import { check_auth } from "./http/userAPI";
import { getCart } from "./http/cart_cartItemAPI";

const App = observer(() => {
  const { user, cart } = useContext(Context); // ОБЯЗАТЕЛЬНО, иначе user не будет доступен
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadAppData = async () => {
      try {
        const userData = await check_auth();
        user.setUser(userData);
        user.setIsAuth(true);

        // Загружаем корзину
        const cartData = await getCart();
        cart.setCart({
          id: cartData.id,
          user_id: cartData.user_id,
        });

        const filteredItems = cartData.CartItems.map((item) => ({
          id: item.id,
          cart_id: item.cart_id,
          product_id: item.product_id,
          quantity: item.quantity,
        }));

        cart.setCartItems(filteredItems);
      } catch {
        user.setIsAuth(false);
      } finally {
        setLoading(false);
      }
      console.log("Cart:", cart.cart); // { id, user_id }
      console.log("Cart Items:", cart.cartItems); // Массив cartItem'ов
    };

    loadAppData();
  }, []);

  if (loading) {
    return <Spinner animation={"grow"} />;
  }

  return (
    <BrowserRouter>
      <NavBar />
      <AppRouter />
    </BrowserRouter>
  );
});

export default App;
