import React, { useState } from "react";
import { Form, Button, Modal } from "react-bootstrap";
import { createCat } from "../../http/product_categoryAPI";

// пропсы 1-ый видно или нет. 2ая фукнция скрывает модальное окно
const CreateCategory = ({ show, onHide }) => {
  const [name, setName] = useState("");

  const create = () => {
    // Проверка перед отправкой: если значение пустое, ничего не отправлять
    if (!name.trim()) {
      return alert("Название категории не может быть пустым!");
    }

    createCat({ category_name: value }).then((data) => setValue("")); // обновляем input если все гуд
    onHide();
  };

  return (
    <Modal size="lg" centered onHide={onHide} show={show}>
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">Добавить категорию</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Control placeholder={"Введите название категории"} />
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="outline-success" value={name} onChange={(e) => setName(e.target.value)} onClick={create}>
          Добавить
        </Button>
        <Button variant="outline-danger" onClick={onHide}>
          Закрыть
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default CreateCategory;
