import React, { useState, useEffect, useContext } from "react";
import { Form, Button, Modal, Dropdown } from "react-bootstrap";
import { Context } from "../../index";
import { getCategory, getProductById, updateProd } from "../../http/product_categoryAPI";
import { observer } from "mobx-react-lite";

const UpdateProduct = observer(({ show, onHide, productId }) => {
  const { product } = useContext(Context); // Считываем категорию из глобального состояния
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [stock, setStock] = useState("");
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    // Получаем категории при открытии модалки
    getCategory().then((data) => setCategories(data));

    // Получаем данные о товаре, если productId задан
    if (productId) {
      getProductById(productId).then((data) => {
        setName(data.name);
        setDescription(data.description);
        setPrice(data.price);
        setStock(data.stock);
        product.setSelectedCategory(data.category); // Загружаем категорию для редактирования
      });
    }
  }, [productId, product]);

  const upProd = async () => {
    try {
      const updatedFields = {};

      // Собираем обновленные данные
      if (name.trim()) updatedFields.name = name;
      if (description.trim()) updatedFields.description = description;
      if (price.trim()) updatedFields.price = parseInt(price);
      if (stock.trim()) updatedFields.stock = parseInt(stock);

      // Добавляем выбранную категорию
      if (product.selectedCategory?.category_id) {
        updatedFields.category_id = product.selectedCategory.category_id;
      }

      // Если нет изменений, то показываем предупреждение
      if (Object.keys(updatedFields).length === 0) {
        alert("Вы не ввели никаких изменений для обновления!");
        return;
      }

      // Обновляем товар
      await updateProd(productId, updatedFields);

      alert("Товар успешно обновлен!");
      onHide(); // Закрываем модалку
    } catch (error) {
      alert(error.response?.data?.message || "Ошибка при обновлении товара");
    }
  };

  return (
    <Modal size="lg" centered show={show} onHide={onHide}>
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">Обновление товара</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Control
            className="mb-2"
            placeholder="Введите новое название продукта"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <Form.Control
            className="mb-2"
            placeholder="Введите новое описание товара"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
          <Form.Control
            className="mb-2"
            type="number"
            min="0"
            placeholder="Введите новую стоимость товара"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
          />
          <Form.Control
            className="mb-2"
            type="number"
            min="0"
            placeholder="Введите количество товара в наличии"
            value={stock}
            onChange={(e) => setStock(e.target.value)}
          />

          <Dropdown className="mb-3">
            <Dropdown.Toggle>{product.selectedCategory?.category_name || "Выберите категорию"}</Dropdown.Toggle>
            <Dropdown.Menu>
              {categories.map((categ) => (
                <Dropdown.Item key={categ.category_id} onClick={() => product.setSelectedCategory(categ)}>
                  {categ.category_name}
                </Dropdown.Item>
              ))}
            </Dropdown.Menu>
          </Dropdown>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="outline-success" onClick={upProd}>
          Обновить данные о продукте
        </Button>
        <Button variant="outline-danger" onClick={onHide}>
          Закрыть
        </Button>
      </Modal.Footer>
    </Modal>
  );
});

export default UpdateProduct;
