import React, { useContext } from "react";
import { observer } from "mobx-react-lite"; // Импортируем observer
import { Container, Row, Col, Card, Image } from "react-bootstrap";
import { Context } from "../index"; // Контекст для доступа к хранилищу

const Cart = observer(() => {
  const { cart, product } = useContext(Context); // Получаем store

  const cartItems = cart.cartItems || []; // Получаем все элементы корзины
  const products = product.product || []; // Получаем все продукты

  return (
    <Container className="mt-4">
      <h2 className="text-center mb-4">Товары в корзине</h2>

      <Row className="justify-content-center">
        {/* Проверка, если корзина пуста */}
        {cartItems.length === 0 ? (
          <div className="text-center">
            <h4>Ваша корзина пуста</h4>
          </div>
        ) : (
          cartItems.map((item) => {
            const productDetails = products.find((prod) => prod.id === item.product_id);

            if (!productDetails) return null; // Если продукт не найден, не отображаем его

            return (
              <Col key={item.id} md={4} className="d-flex mb-4">
                <Card className="w-100 border-secondary">
                  <Image
                    src={process.env.REACT_APP_API_URL + productDetails.image} // Путь к изображению товара
                    alt={productDetails.name} // Название товара для alt текста
                    style={{ width: "100%", height: 200, objectFit: "contain", borderRadius: 10 }}
                    className="p-2"
                  />
                  <Card.Body className="d-flex flex-column">
                    <Card.Title className="mb-1">{productDetails.name}</Card.Title> {/* Название товара */}
                    <Card.Text className="flex-grow-1">
                      <strong>Описание:</strong> {productDetails.description} {/* Описание товара */}
                      <br />
                      <strong>Цена:</strong> {productDetails.price} ₽ {/* Цена товара */}
                      <br />
                      <strong>Количество:</strong> {item.quantity} шт. {/* Количество товара в корзине */}
                    </Card.Text>
                  </Card.Body>
                </Card>
              </Col>
            );
          })
        )}
      </Row>
    </Container>
  );
});

export default Cart;
