import React, { useContext, useState } from "react";
import { Form, Button, Modal, Dropdown } from "react-bootstrap";
import { Context } from "../..";

const CreateProduct = ({ show, onHide }) => {
  const { product } = useContext(Context);

  const [file, setFile] = useState(null);
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState(0);
  const [stock, setStock] = useState("");
  const [category, setCategory] = useState(null);

  const selectFile = (e) => {
    console.log(e.target.files);
  };

  return (
    <Modal size="lg" centered onHide={onHide} show={show}>
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">Добавить продукт</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Control className="mb-2" type="file" onChange={selectFile} />

          <Form.Control className="mb-2" placeholder={"Введите название продукта"} />
          <Form.Control className="mb-2" placeholder={"Введите описание товара"} />
          <Form.Control className="mb-2" type="number" min="0" placeholder={"Введите стоимость товара "} />
          <Form.Control className="mb-2" placeholder={"Введите, какое количество товара имеется в наличии"} />

          <Dropdown className="mb-3">
            <Dropdown.Toggle>Выберите категорию товара</Dropdown.Toggle>
            <Dropdown.Menu>
              {product.category.map((type) => (
                <Dropdown.Item key={type.id}>{type.category_name}</Dropdown.Item>
              ))}
            </Dropdown.Menu>
          </Dropdown>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="outline-success" onClick={onHide}>
          Добавить
        </Button>
        <Button variant="outline-danger" onClick={onHide}>
          Закрыть
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

