import React, { useContext } from "react";
import { Form, Button, Modal, Dropdown } from "react-bootstrap";
import { Context } from "../..";

// пропсы 1-ый видно или нет. 2ая фукнция скрывает модальное окно
const createProduct = ({ show, onHide }) => {
  const { product } = useContext(Context);
  return (
    <Modal size="lg" centered onHide={onHide} show={show}>
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">Добавить устройство</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Dropdown>
          <Dropdown.Toggle>Выберите категорию товара</Dropdown.Toggle>
          <Dropdown.Menu>
            {product.category.map((type) => (
              <Dropdown.Item key={type.id}>{type.category_name}</Dropdown.Item>
            ))}
          </Dropdown.Menu>
        </Dropdown>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="outline-success" onClick={onHide}>
          Добавить
        </Button>
        <Button variant="outline-danger" onClick={onHide}>
          Закрыть
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default createProduct;
