import React from "react";
import { Container, Card, Row, Col, Image, Badge } from "react-bootstrap";

// Пример данных заказов
const orders = [
  {
    id: 101,
    total: 1500,
    status: "pending",
    items: [
      {
        id: 1,
        name: "Аккумулятор DELTA CT12201",
        price: 6300,
        quantity: 2,
        image: "https://via.placeholder.com/150", // заглушка для картинки
      },
      {
        id: 2,
        name: "Свеча зажигания NGK",
        price: 800,
        quantity: 4,
        image: "https://via.placeholder.com/150",
      },
    ],
  },
  {
    id: 102,
    total: 3200,
    status: "shipped",
    items: [
      {
        id: 3,
        name: "Масло моторное Mobil 1",
        price: 3200,
        quantity: 1,
        image: "https://via.placeholder.com/150",
      },
    ],
  },
];

const getStatusVariant = (status) => {
  switch (status) {
    case "pending":
      return "warning";
    case "shipped":
      return "info";
    case "delivered":
      return "success";
    case "cancelled":
      return "danger";
    default:
      return "secondary";
  }
};

const OrdersPage = () => {
  return (
    <Container className="mt-4">
      <h2>Мои заказы</h2>
      <Row className="mt-3">
        {orders.map((order) => (
          <Card className="mb-4" key={order.id}>
            <Card.Body>
              <Card.Title>
                Заказ №{order.id} — <Badge bg={getStatusVariant(order.status)}>{order.status.toUpperCase()}</Badge>
              </Card.Title>
              <Card.Subtitle className="mb-2 text-muted">Сумма заказа: {order.total} ₽</Card.Subtitle>

              <Row className="mt-3">
                {order.items.map((item) => (
                  <Col md={6} key={item.id} className="d-flex mb-3">
                    <Image src={item.image} alt={item.name} width={100} height={100} rounded className="me-3" />
                    <div>
                      <h5>{item.name}</h5>
                      <p className="mb-1">Цена: {item.price} ₽</p>
                      <p className="mb-1">Количество: {item.quantity} шт.</p>
                    </div>
                  </Col>
                ))}
              </Row>
            </Card.Body>
          </Card>
        ))}
      </Row>
    </Container>
  );
};

export default OrdersPage;
